import sql from 'mssql';

const db_seting = {
    user: 'Adminphpmyadmin',
    password: 'S8KPRY$b',
    server: 'ercolua.database.windows.net',
    database:'mpdb',
    options: {
        encrypt : true,
        trustServerCertificate: true,
    }
};

export async function conectioDb(){
    try {
        const pool = await sql.connect(db_seting);
        return pool;
    } catch (error) {
        console.log(error)
    }
}
export { sql };

